mapper.update:
UPDATE dvd_basket
SET buy=1, count=#{count}, regdate=SYSDATE
WHERE id=#{id}
AND dvd_num=#{dvd_num}
AND buy=#{buy}

mapper.delete:
DELETE FROM dvd_basket
WHERE id=#{id}
AND dvd_num=#{dvd_num}
AND buy=#{buy}

mapper.select:
SELECT id, dvd_num
FROM dvd_basket
WHERE id=#{id}
AND dvd_num=#{dvd_num}
AND buy=#{buy}
