mapper.update:
UPDATE qna
SET qna_answer=#{qna_answer}
WHERE qna_num=#{qna_num}

UPDATE qna
SET qna_title=#{qna_title}, qna_content=#{qna_content}
WHERE qna_num=#{qna_num}

UPDATE qna
SET qna_answer=null
WHERE qna_num=#{qna_num}

mapper.select:
SELECT qna_num, qna_writer, qna_title, qna_content, dvd_num, qna_answer, regdate
FROM qna
WHERE qna_num = #{qna_num}
